import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const HomePage = () => {
  const { user, logout } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState("all");
  const [sort, setSort] = useState("date");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/tasks");
        setTasks(response.data);
      } catch (error) {
        console.error("Error fetching tasks:", error);
      }
    };

    fetchTasks();
  }, []);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/tasks/${id}`);
      setTasks(tasks.filter((task) => task.id !== id));
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === "completed") return task.completed;
    if (filter === "incomplete") return !task.completed;
    return true;
  });

  const sortedTasks = filteredTasks.sort((a, b) => {
    if (sort === "date") return new Date(b.createdAt) - new Date(a.createdAt);
    if (sort === "name") return a.name.localeCompare(b.name);
    return 0;
  });

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container">
      <h2>Welcome, {user.username}</h2>
      <button onClick={handleLogout}>Logout</button>
      <button onClick={() => navigate("/profile")}>Profile</button>
      <h3>Your Tasks</h3>
      <button onClick={() => navigate("/add-task")}>Add Task</button>
      <div>
        <label>Filter: </label>
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="incomplete">Incomplete</option>
        </select>
        <label>Sort: </label>
        <select value={sort} onChange={(e) => setSort(e.target.value)}>
          <option value="date">Date</option>
          <option value="name">Name</option>
        </select>
      </div>
      <ul>
        {sortedTasks.map((task) => (
          <li key={task.id}>
            {task.name} - {task.completed ? "Completed" : "Incomplete"}
            <button onClick={() => navigate(`/task/${task.id}`)}>View Details</button>
            <button onClick={() => navigate(`/edit-task/${task.id}`)}>Edit</button>
            <button onClick={() => handleDelete(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HomePage;
